  .wikitable > caption { padding: 5px; font-size: 10pt; }
  .wikitable > tr > th, .wikitable > tr > td, .wikitable > * > tr > th, .wikitable > * > tr > td { border: 1pt solid rgb(170, 170, 170); padding: 0.4em 0.6em; background: rgb(255, 255, 255) !important; color: rgb(0, 0, 0) !important; }
  .wikitable > tr > th, .wikitable > * > tr > th { text-align: center; }
  table.listing, table.listing td { border: 1pt solid rgb(0, 0, 0); border-collapse: collapse; }
  .catlinks ul { display: inline; padding: 0px; list-style: none; }
  .catlinks li { display: inline-block; line-height: 1.15; margin: 0.1em 0px; border-left: 1pt solid rgb(170, 170, 170); padding: 0px 0.4em; }
  .catlinks li:first-child { border-left: 0px; padding-left: 0.2em; }
  .mw-hidden-catlinks, .catlinks { display: none; }
  .mw-editsection, .mw-editsection-like, .mw-indicators, #siteNotice, .usermessage { display: none; }
  .printfooter { padding: 1em 0px; }
  .center { text-align: center; }
  a { background: none !important; padding: 0px !important; }
  a, a.external, a.new, a.stub { color: inherit !important; text-decoration: inherit !important; }
  dt { font-weight: bold; }
